sp_widget.client_script:
function initializeMap() {
    AddArcGISLibrary();
    var c = this;

    setTimeout(function() {
        require(["esri/config", "esri/Map", "esri/views/MapView", "esri/rest/locator", "esri/widgets/Zoom", "esri/widgets/Search"],
            function(esriConfig, Map, MapView, locator, Zoom, Search) {

                esriConfig.apiKey = c.data.apiKey;

                var map = new Map({
                    basemap: "streets"
                });
                //view.zoom.position = "top-right"
                var view = new MapView({
                    container: "viewDiv",
                    map: map,
                    zoom: 14,
                    center: [-117.63321, 33.61281]

                });

                view.ui.move(["zoom"], "bottom-right");

                var serviceUrl = "http://geocode-api.arcgis.com/arcgis/rest/services/World/GeocodeServer";
                view.on("click", function(evt) {
                    var params = {
                        location: evt.mapPoint
                    };
                    search.searchTerm = "";
                    locator.locationToAddress(serviceUrl, params).then(
                        function(response) {
                            // Show the address found
                            var address = response.address;
                            showAddress(address, evt.mapPoint);

                        },
                        function(err) {
                            // Show no address found
                            showAddress("No address found.", evt.mapPoint);
                        }
                    );

                });

                function showAddress(address, pt) {


                    view.openPopup({
                        content: +Math.round(pt.longitude * 100000) / 100000 + ", " + Math.round(pt.latitude * 100000) / 100000,
                        //content: address,
                        //title: "Search Resulting",
                        title: address,
                        location: pt
                    });
									setVariables(pt.latitude,pt.longitude, address);
                }

                var search = new Search({ //Add Search widget
                    view: view,
                    resultGraphicEnabled: false,
                    popupEnabled: false
                });
                search.on("search-complete", function(event) {
                    showAddress(event.results[0].results[0].name, event.results[0].results[0].feature.geometry);
                });
                view.ui.add(search, "top-left"); //Add to the map
            });
    }, 2000);
}

function setVariables(latIn, LonIn, locaIn) {
    var lon = Math.round(latIn * 100000) / 100000;
    var lat = Math.round(LonIn * 100000) / 100000;

    $scope.page.g_form.setValue("latitude", lon);
    $scope.page.g_form.setValue("longitude", lat);
    $scope.page.g_form.setValue("location", locaIn);
}

function AddArcGISLibrary() {
    var arcGISLib = document.createElement('script');
    arcGISLib.setAttribute('src', 'https://js.arcgis.com/4.27');
    document.head.appendChild(arcGISLib);
}

sp_widget.css:
#viewDiv {
  height: 100%;
  width: 100%;
  min-height: 350px;
  min-width: 800px;
  font-family: Arial, Helvetica, sans-serif;
  font-size: 14px;
  color: #323232;

}

